    ],
    "strategy_set": [
      "显式状态机校验(禁止非法流转)",
      "Outbox事件表+异步投递(最终一致性)",
      "幂等键(requestId)+去重表"
    ],
    "strategy_ids": [
      "state_machine",
      "outbox",
      "idempotency_key"
    ],
    "components": [
      "OrderStateGuard(新增):统一状态校验/流转入口",
      "OrderAuditService(新增):状态变更审计",
      "ReconcileJob(可选):对账/补偿任务",
      "StateMachineConfig(新增):allowedTransitions配置+统一校验入口",
