    ],
    "strategy_set": [
      "显式状态机校验(禁止非法流转)",
      "Outbox事件表+异步投递(最终一致性)",
      "幂等键(requestId)+去重表"
    ],
    "strategy_ids": [
      "state_machine",
      "outbox",
      "idempotency_key"
    ],
