  "design": {
    "architecture_overview": "Based on the existing layered architecture (Controller/Service/Mapper), introduce minimal closed-loop components in the mixed domain and reuse existing patterns.",
    "evidence_hints": [
      "Focus on entry methods:isValid, getAllCriteria, getCriteria, addCriterion",
      "Layers hinted:other",
      "Evidence files:mall-mbg/src/main/java/com/macro/mall/model/UmsResourceExample.java, mall-mbg/src/main/java/com/macro/mall/model/SmsHomeBrandExample.java",
      "Requirement type:rule-validation-audit (Concurrency control and locking)"
    ],
    "strategy_set": [
      "Explicit state machine validation(block illegal transitions)",
      "Outbox table + async dispatcher(eventual consistency)",
      "Idempotency key(requestId) + dedup table"
    ],
    "strategy_ids": [
      "state_machine",
      "outbox",
      "idempotency_key"
    ],
    "components": [
      "OrderStateGuard(new): centralized state validation/transition entry",
      "OrderAuditService(new): audit trail for state changes",
      "RuleValidatorRegistry(new): plug-in validators for rule 'Concurrency control and locking'",
      "ErrorSemanticCatalog(new): stable error codes and standardized semantics",
      "RuleAuditService(new): audit rule evaluation(inputs/outputs/errors)",
      "StateMachineConfig(new): allowedTransitions + centralized guard",
      "OutboxEventService(new): persist events and dispatch asynchronously",
      "IdempotencyService(new): validate idempotency key and dedup records"
    ],
