  "design": {
    "architecture_overview": "Based on the existing layered architecture (Controller/Service/Mapper), introduce minimal closed-loop components in the order domain and reuse existing patterns.",
    "evidence_hints": [
      "Focus on entry methods:addCriterion, andIdIsNull, andIdIsNotNull, isValid",
      "Evidence files:mall-mbg/src/main/java/com/macro/mall/model/CmsPrefrenceAreaProductRelationExample.java, mall-mbg/src/main/java/com/macro/mall/model/UmsMemberTaskExample.java"
    ],
    "strategy_set": [
      "Explicit state machine validation(block illegal transitions)",
      "Outbox table + async dispatcher(eventual consistency)",
      "Idempotency key(requestId) + dedup table"
    ],
    "strategy_ids": [
      "state_machine",
      "outbox",
      "idempotency_key"
    ],
    "components": [
      "OrderStateGuard(new): centralized state validation/transition entry",
      "OrderAuditService(new): audit trail for state changes",
      "ReconcileJob(optional): reconciliation/compensation job",
      "StateMachineConfig(new): allowedTransitions + centralized guard",
      "OutboxEventService(new): persist events and dispatch asynchronously",
      "IdempotencyService(new): validate idempotency key and dedup records"
    ],
    "apis": [
      "POST /order/state/transition: centralized transition entry(carries requestId)",
      "GET /order/audit/{orderId}: query audit records"
